import groovy.json.JsonSlurper
responseContent =testRunner.testCase.getTestStepByName("GetTransaction").getPropertyValue("response") slurperresponse = new JsonSlurper().parseText(responseContent) String partnerRate=slurperresponse.content.partnerRate[0] Double shipperRate=slurperresponse.content.shipperRate[0] String buyRate=slurperresponse.content.buyRate[0] Double amount=slurperresponse.content.amount[0] String PrintTransactionId =slurperresponse.content.clientTransactionId[0] String accountNumber =slurperresponse.content.accountNumber[0] String authorizationCode= slurperresponse.content.authorizationCode[0] Double currentBalance = slurperresponse.content.currentBalance[0] String shipperId= slurperresponse.content.shipperId[0] String shipperName = slurperresponse.content.shipperName[0] String partnerId = slurperresponse.content.partnerId[0] String partnerName = slurperresponse.content.partnerName[0] String shipmentId = slurperresponse.content.shipmentId[0]
log.info ("partnerRate: " +partnerRate) log.info ("shipperRate: "+shipperRate) log.info("buyRate: "+buyRate) log.info("amount: "+amount) log.info("PrintTransactionId: "+PrintTransactionId) log.info("accountNumber: " + accountNumber) log.info( "authorizationCode: "+authorizationCode) log.info( "currentBalance: "+currentBalance) log.info( "shipperId: "+shipperId) log.info( "shipperName: "+shipperName) log.info( "partnerId: "+partnerId) log.info( "partnerName:"+partnerName) log.info( "shipmentId: "+shipmentId)
testRunner.testCase.testSuite.project.setPropertyValue("partnerRate", partnerRate) testRunner.testCase.testSuite.project.setPropertyValue("shipperRate", shipperRate.toString()) testRunner.testCase.testSuite.project.setPropertyValue("buyRate", buyRate) testRunner.testCase.testSuite.project.setPropertyValue("amount", amount.toString()) testRunner.testCase.testSuite.project.setPropertyValue("PrintTransactionId", PrintTransactionId) testRunner.testCase.testSuite.project.setPropertyValue("accountNumber", accountNumber) testRunner.testCase.testSuite.project.setPropertyValue( "authorizationCode", authorizationCode) testRunner.testCase.testSuite.project.setPropertyValue( "currentBalance",currentBalance.toString()) testRunner.testCase.testSuite.project.setPropertyValue( "shipperId",shipperId) testRunner.testCase.testSuite.project.setPropertyValue( "shipperName",shipperName) testRunner.testCase.testSuite.project.setPropertyValue( "partnerId",partnerId) testRunner.testCase.testSuite.project.setPropertyValue( "partnerName",partnerName) testRunner.testCase.testSuite.project.setPropertyValue( "ShipmentID",shipmentId)
// Create Shortpay attributes to pass in create ShortPay Ledger Transaction Double shortPayAmount = amount-1;
String shortPayshipperRate = shortPayAmount;
log.info("ShortPayAmount: "+ shortPayAmount) log.info("ShortPayshipperRate: "+shortPayshipperRate) testRunner.testCase.testSuite.project.setPropertyValue("ShortPayAmount", shortPayAmount.toString()) testRunner.testCase.testSuite.project.setPropertyValue("ShortPayshipperRate", shortPayshipperRate.toString())

