log.info(System.getProperty("webdriver.chrome.driver"));

WebDriver driver = new ChromeDriver();
//driver.manage().timeouts().implicitlyWait(10, TimeUnit.SECONDS);
//WebDriverWait wait = new WebDriverWait(driver, 15);
driver.manage().window().maximize();

log.info("Browser is launched");

log.info(getContent);

driver.get(getContent);


Thread.sleep(1000)

log.info("Label URL is hit")

log.info("Page is: " + driver.getTitle())

readExcel()
  {
    Workbook workbook1 = Workbook.getWorkbook(new File("D:\\SOAPTestData\\DataPartitioning\\New USPS Rates.xlsx"))
    Sheet sheet1 = workbook1.getSheet(0)
    Cell a1 = sheet1.getCell(0,2)
    Cell b2 = sheet1.getCell(2,2)
    Cell c2 = sheet1.getCell(2,1)

    println a1.getContents();
    println b2.getContents();
    println c2.getContents();  

    workbook1.close()
  }

def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}
def randomno=generator( (('A'..'Z')+('0'..'9')).join(), 10 )
testRunner.testCase.testSuite.project.setPropertyValue("TransactionID_Label","53448601_"+randomno)
log.info("53448601_"+randomno);

