con:configuration.scriptText:
def transactionId = context.expand( '${Save_SBR_Printed_Report#Response#$[\'transactionId\']}' )
def v3_report_transactionId = context.expand( '${Get_v3_Reports#Response#$[\'content\'][0][\'transactionId\']}' )

def printStatus_SaveReport = context.expand( '${Save_SBR_Printed_Report#Response#$[\'printStatus\']}' )
def printStatus_GETv3 = context.expand( '${Get_v3_Reports#Response#$[\'content\'][0][\'printStatus\']}' )




assert transactionId == v3_report_transactionId : "transactionId  assertion Failed"
assert printStatus_SaveReport == printStatus_GETv3 : "printStatus  assertion Failed"

