if (accountNumber != SBR_PaymentAccountNumber) {error.add("Assertion failed for accountNumber")};
if (externalId != "33333") {error.add("Assertion failed for externalId")};
if (adjustmentReason != "null") {error.add("Assertion failed for adjustmentReason")};
