assert LedgerAccountBalance == v4_Reports_balance : "Account Balance assertion Failed"

con:config.script:
def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}
def randomno=generator( (('A'..'Z')+('0'..'9')).join(), 10 )
testRunner.testCase.testSuite.project.setPropertyValue("TransactionID_SBR","ACHTest_"+randomno)
log.info("ACHTest_"+randomno);

