if (amount !="300") {error.add("Assertion failed for amount")};
if (status !="Processed") {error.add("Assertion failed for status")};
if (description !="Postage Fund Requested") {error.add("Assertion failed for description")};
if (paymentSystemAuthCode != "94857475849388") {error.add("Assertion failed for paymentSystemAuthCode")};


assert error.size() == 0, error;
if(error.size() == 0){ log.info "Assertions passed for all the reporting fields" };
