int noofElements=slurperresponse.numberOfElements

log.info("No of record"+ noofElements )
File  f =new File ("C:\\Users\\NEX4PPS\\Desktop\\test1.txt") 
for (int i=0; i<noofElements;i++) 
{
log.info(i);
x=slurperresponse.content[i].transactionId
f.append(System.getProperty("line.separator")+x)
log.info(x)
