def httpResponseHeaders = context.testCase.testSteps["Print_Shipment"].testRequest.response.responseHeaders."X-PB-TransactionId"[0]
def devId =  context.testCase.testSteps["Print_Shipment"].testRequest.response.responseHeaders."X-PB-IntegratorId"[0]
