log.info(transactionId);
testRunner.testCase.testSuite.project.setPropertyValue("TransactionId_SAM",transactionId)
//log.info (httpResponseHeaders)


import groovy.json.JsonSlurper

responseContent =testRunner.testCase.getTestStepByName("Print_Shipment").getPropertyValue("response")
slurperresponse = new JsonSlurper().parseText(responseContent)
contents=slurperresponse.documents[0].contents
parcelTrackingNumber=slurperresponse.parcelTrackingNumber

def special_services = context.expand( '${Print_Shipment#Response#$[\'rates\'][0][\'specialServices\']}')

def shipment_options = context.expand( '${Print_Shipment#Response#$[\'shipmentOptions\']}' )

def delivery_commitment = context.expand( '${Print_Shipment#Response#$[\'rates\'][0][\'deliveryCommitment\']}' )

def dimensionalWeight = context.expand( '${Print_Shipment#Response#$[\'rates\'][0][\'dimensionalWeight\'][\'weight\']}' )


// def reference_fields

// console logs
log.info (contents)
log.info (parcelTrackingNumber)
log.info (special_services)
log.info(shipment_options);
log.info (delivery_commitment)
log.info (dimensionalWeight)
//log.info (reference_fields)


// set properties
testRunner.testCase.testSuite.project.setPropertyValue("Contents", contents)
testRunner.testCase.testSuite.project.setPropertyValue("parcelTrackingNumber", parcelTrackingNumber)
testRunner.testCase.testSuite.project.setPropertyValue("special_services", special_services)
testRunner.testCase.testSuite.project.setPropertyValue("shipment_options", shipment_options)
testRunner.testCase.testSuite.project.setPropertyValue("delivery_commitment", delivery_commitment)
testRunner.testCase.testSuite.project.setPropertyValue("dimensionalWeight", dimensionalWeight)
//testRunner.testCase.testSuite.project.setPropertyValue("reference_fields", reference_fields)

